mapper.select:
select id,channel_id,play_pv,play_uv,FORMAT(five_second_pv / (five_second_pv+five_ninety_pv+greater_ninety_pv)*100,2) as five_second_pv,
FORMAT(five_ninety_pv / (five_second_pv+five_ninety_pv+greater_ninety_pv)*100,2) as five_ninety_pv,
FORMAT(greater_ninety_pv / (five_second_pv+five_ninety_pv+greater_ninety_pv)*100,2) as greater_ninety_pv,
(play_t_time / play_pv) as play_t_time,comments,praise_num,share_num,barrage_num,t
from

  sb_tj_channel_hour_report


  sb_tj_channel_day_report



    and channel_id = #{id,jdbcType=INTEGER}


    and t  >= #{startTime,jdbcType=TIMESTAMP}


    and t  < #{endTime,jdbcType=TIMESTAMP}



  order by ${orderBy} ${sequence}



    limit ${offset}, ${limit}


    limit ${limit}

select count(*) from

  sb_tj_channel_hour_report


  sb_tj_channel_day_report



    and channel_id = #{id,jdbcType=INTEGER}


    and t  >= #{startTime,jdbcType=TIMESTAMP}


    and t  < #{endTime,jdbcType=TIMESTAMP}

select id,channel_id,sum(play_pv) as play_pv,sum(play_uv) as play_uv,
FORMAT(sum(five_second_pv) / (sum(five_second_pv)+sum(five_ninety_pv)+sum(greater_ninety_pv))*100,2) as five_second_pv,
FORMAT(sum(five_ninety_pv) / (sum(five_second_pv)+sum(five_ninety_pv)+sum(greater_ninety_pv))*100,2) as five_ninety_pv,
FORMAT(sum(greater_ninety_pv) / (sum(five_second_pv)+sum(five_ninety_pv)+sum(greater_ninety_pv))*100,2) as greater_ninety_pv,
(sum(play_t_time) / sum(play_pv)) as play_t_time,sum(comments) as comments,sum(praise_num) as praise_num,sum(share_num) as share_num,
sum(barrage_num)as barrage_num,t
from

  sb_tj_channel_hour_report


  sb_tj_channel_day_report



    and channel_id = #{id,jdbcType=INTEGER}


    and t  >= #{startTime,jdbcType=TIMESTAMP}


    and t  < #{endTime,jdbcType=TIMESTAMP}


group by channel_id

  order by ${orderBy} ${sequence}



    limit ${offset}, ${limit}


    limit ${limit}

select count(*) from (select channel_id from

  sb_tj_channel_hour_report


  sb_tj_channel_day_report



    and channel_id = #{id,jdbcType=INTEGER}


    and t  >= #{startTime,jdbcType=TIMESTAMP}


    and t  < #{endTime,jdbcType=TIMESTAMP}


group by channel_id
) as A

